"use client"
import React, {FC, ReactElement, useState} from 'react';
import Calculator from "@/components/Calculator/Calculator";
import Galery from "@/components/Galery/Galery";
import Image from "next/image";

import calculator from "@/public/calculator.svg";
import galery from "@/public/easel.svg";
import arrow_left from "@/public/arrow-left.svg";
import power from "@/public/power.svg";

const images: string[] = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
]

interface Component {
    src: string;
    alt: string;
    component: ReactElement;
}

const components: Component[] = [
    {
        src: calculator,
        alt: "calculator",
        component: <Calculator />,
    },
    {
        src: galery,
        alt: "galery",
        component: <Galery images={images} />,
    }
];

const OS: React.FC = () => {
    const [activeComponent, setActiveComponent] = useState<string | null>(null);
    const [isOn, setIsOn] = useState(true);
    const [message, setMessage] = useState<string | null>(null);

    const togglePower = () => {
        if (isOn) {
            setMessage("Goodbye!");
            setTimeout(() => {
                setIsOn(false);
                setMessage(null); // Clear message after turning off
            }, 1500); // Delay for animation
        } else {
            setMessage("Hello!");
            setIsOn(true);
            setTimeout(() => {
                setMessage(null); // Clear message after turning on
            }, 1500); // Delay for animation
        }
    };

    return (
        <>
            <Image
                src={power}
                alt={"power"}
                style={{height: "50px", width: "50px", position: "fixed", top: "50px", left: "50px"}}
                onClick={togglePower}
            />
            <div style={{textAlign: 'center', height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column"}}>
                {message ? (
                    <h1>{message}</h1>
                ) : (
                    isOn ? (
                        <>
                            <div style={{marginBottom: '20px'}}>
                                {activeComponent ? (
                                    <Image
                                        style={{width: "50px", height: "50px"}}
                                        src={arrow_left}
                                        alt="arrow"
                                        onClick={() => setActiveComponent(null)}
                                    />
                                ) : (
                                    <>
                                        {components.map((item, index) => (
                                            <Image
                                                key={index}
                                                style={{width: "50px", height: "50px"}}
                                                src={item.src}
                                                alt={item.alt}
                                                onClick={() => setActiveComponent(item.alt)}
                                            />
                                        ))}
                                    </>
                                )}
                            </div>
                            <div>
                                {activeComponent && components.find(item => item.alt.toLowerCase() === activeComponent)?.component}
                            </div>
                        </>
                    ) : (
                        <div>
                            <h1>Your computer is turned off</h1>
                        </div>
                    )
                )}
            </div>
        </>
    );
};

export default OS;